sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if ( newValue === '') {
      return;
   }

	var whatneeded = g_form.getValue('u_what_needed');

   //Clear all of Choices from the What needed Field choice list
	g_form.clearOptions('u_what_needed');

	//If the value of Request Type field is hr, add
	//Two hr Choices and other to What needed field choice list

	if(newValue == 'hr'){
		g_form.addOption('u_what_needed','hr1','Human Resources1');
		g_form.addOption('u_what_needed','hr2','Human Resources2');
		g_form.addOption('u_what_needed','other','Other');
	}

	//If the value of Request Type is facilities, add
	//Two facilities Choices and other to What needed field choice list

	if(newValue == 'facilities'){
		g_form.addOption('u_what_needed','facilities1','Facilities1');
		g_form.addOption('u_what_needed','facilities2','Facilities2');
		g_form.addOption('u_what_needed','other','Other');
	}

	//If the value of Request Type is legal, add
	//Two legal Choices and other to What needed field choice list

	if(newValue == 'legal'){
		g_form.addOption('u_what_needed','legal1','Legal1');
		g_form.addOption('u_what_needed','legal2','Legal2');
		g_form.addOption('u_what_needed','other','Other');
	}

	//If the Form is Loading and it is not a New Record, set u_what_needed value to value from the record Before , it was Loaded.

	if(isLoading && !g_form.isNewRecord()){
		g_form.setValue('u_what_needed', whatneeded);
	}

}
